import React from "react";
import axios from "axios"
import {Card, Header, Icon, Form, Input} from "semantic-ui-react"
import "./ToDoList.css"

let endPoint = "http://localhost:9000"
export default function ToDoList(){
    const [state, setState] = React.useState({
        task:"",
        items:[],
    })

    React.useEffect(()=>{
        getTask()
    })

    function onChange(event){
        setState({
            [event.target.name] : event.target.value,
        })
    }

    function onSubmit(event){
        event.preventDefault()
        let task = state.task
        if(task){
            axios.post(endPoint+"/api/tasks", 
            {task,},
            {headers:{
                "Content-Type": "applicationx-www-form-urlencoded",
                },
            }).then((res)=>{
                console.log(res)
                //Updating the page 
                getTask()

                setState({
                        task:"",
                })

            })
        }
    }

    function getTask(){
        axios.get(endPoint+"/api/task").then((res=>{
            if(res.data){
                setState({
                    tesk:"",
                    items: res.data.map((item)=>{
                        let color= "yellow"
                        let style = {
                            wordWrap: "break-word",
                        }

                        if(item.status){
                            color = "green"
                            style["textdecoration"] = "line-through"
                        }
                        return(
                            <Card key={item._id} color={color} className="rough" fluid>
                                <Card.Content>
                                    <Card.Header textAlign="left">
                                        <div style={style}>{item.task}</div>
                                    </Card.Header>
                                    <Card.Meta textAlign="right">
                                        <Icon name="check circle"
                                              color="blue"
                                              onClick={()=>updateTask(item._id)}
                                        />
                                        <span style={{paddingRight:10}}>Done</span>

                                        <Icon name="undo alternate"
                                              color="yellow"
                                              onClick={()=>undoTask(item._id)}
                                        />
                                        <span style={{paddingRight:10}}>Undo</span>
                                        <Icon name="delete"
                                              color="red"
                                              onClick={()=>deleteTask(item._id)}
                                        />
                                        <span style={{paddingRight:10}}>Delete</span>
                                    </Card.Meta>
                                </Card.Content>
                            </Card>
                        )
                    })
                })
            }
        }))
    }

    function undoTask(id){
        axios.put(endPoint+"/api/undoTask"+id,{
            headers:{
                "Content-Type":"application/x-www-form-urlencoded",
            },
        }).then((res)=>{
            console.log(res)
            getTask()
        })
    }

    function updateTask(id){
        axios.put(endPoint+"/api/tasks/"+id,{
            headers:{
                "Content-Type":"application/x-www-form-urlencoded",
            },
        }).then((res)=>{
            console.log(res)
            getTask()
        })
    }

    function deleteTask(id){
        axios.delete(endPoint+"/api/deleteTask/"+id,{
            headers:{
                "Content-Type":"application/x-www-form-urlencoded",
            },
        }).then((res)=>{
            console.log(res)
            getTask()
        })
    }



    return(
        <div>
            <div className="row">
                <Header className="header" as="h2" color="yellow">
                    To Do List
                </Header>
            </div>
            <div className="row">
                <Form onSubmit={onSubmit}>
                    <Input type="text" name="task"
                           onChange={onChange} 
                           value={state.task}
                           fluid
                           placeholder="Create a todo"
                    />
                </Form>
            </div>

            <div className="row">
                <Card>
                    <Card.Group>{state.items}</Card.Group>
                </Card>

            </div>
        </div>
    )
}
